var comlink=document.querySelector(".communication-form__link"),popup=document.querySelector(".input"),close=popup.querySelector(".button--close"),shadow=document.querySelector(".shadow");comlink.addEventListener("click",function(o){o.preventDefault(),shadow.classList.add("shadow-show"),popup.classList.add("input-show")}),close.addEventListener("click",function(o){o.preventDefault(),popup.classList.remove("input-show"),shadow.classList.remove("shadow-show")}),window.addEventListener("keydown",function(o){27===o.keyCode&&popup.classList.contains("input-show")&&(popup.classList.remove("input-show"),shadow.classList.remove("shadow-show"))});
